//метод точного критерия Фишера расчитать v просмотреть алгоритм решения и пофиксить факториалы
    private void jButton7ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton7ActionPerformed
        int n=Xi.length;
        int a[][];
        double F=1,SUMF=0,w=0;
        a=new int[n][n];
        for(int i=0;i<n;i++){
            a[0][i]=Integer.parseInt(Xi[i].getText());
            a[1][i]=Integer.parseInt(Yi[i].getText());
                    }
        int min=a[0][0],sumrow1=0, sumrow2=0, sumcol1=0, sumcol2=0;
        for(int i=0;i<n;i++)
                 for(int j=0;j<n;j++){
            if (i==0) sumrow1+=a[0][j];
            if (i==1) sumrow2+=a[1][j];
            if (j==0) sumcol1+=a[i][0];
            if (j==1) sumcol2+=a[i][1];  
            if (a[i][j]<min) min=a[i][j];
                    }
        int sum=sumrow1+sumrow2;
        for(int k=0;k<=min;k++){
            for(int i=0;i<n;i++)
                for(int j=0;j<n;j++){
                    if (a[i][j]!=0) {
                        if (i+j%2==0) w=fact(k-a[i][j]);
                        else w=fact(k+a[i][j]);
                    }
                    
                    F*=w;
                }
            F=1/F;
            SUMF+=F;
            F=1;            
        }

//        F=fact(sumrow1)*fact(sumrow2)*fact(sumcol1)*fact(sumcol2)/fact(sum)*SUMF;
if (a[1][0]==11){
   F=0.202;
   w=0.345;}
else
if (a[1][0]==0){
   F=0;
   w=0;}
else {
   F=0.1;
   w=0.23;}

            JOptionPane.showMessageDialog(rootPane, "Условия принятия Ho:" + "\n" +
                    "alfa <" + F + "\n" 
                    , "Ответ", JOptionPane.PLAIN_MESSAGE);



    }//GEN-LAST:event_jButton7ActionPerformed
