t = Math.round(t*10000)/10000+Math.round(t*10000)%10000;
t=      (float) 0.7298;
if (Math.abs(r) < t)  JOptionPane.showMessageDialog(rootPane, "Значение r=" + r +
                    ". Критерий незначимости: | r | < " + t +
        ". Принять: значимый.", "Ответ", JOptionPane.OK_OPTION);
else
JOptionPane.showMessageDialog(rootPane, "Значение r=" + r +
                    ". Критерий незначимости: | r | < " + t +
        ". Принять: значимый.", "Ответ", JOptionPane.OK_OPTION);    
                  
                  break;
